// SUPERCODE ENHANCED PRODUCT PROCESSOR
const response = $json.message?.content || $json.response || $json.text || '';

// Extract product data using advanced parsing
const products = [];
const sections = response.split(/\d+\.|Product\s+\d+/i);

for (let i = 1; i < sections.length; i++) {
  const section = sections[i].trim();
  if (!section) continue;

  const product = {
    id: `bp_${Date.now()}_${i}`,
    created: new Date().toISOString(),
    source: 'deepseek_ai'
  };

  // Extract name (first line or after colon)
  const nameMatch = section.match(/^[^
]*?:?\s*(.+?)(?:
|$)/);
  product.name = nameMatch ? nameMatch[1].trim().replace(/["\*]/g, '') : `Balding Pig Product ${i}`;

  // Extract category
  const categoryMatch = section.match(/(t-shirt|hoodie|mug|cap|stickers)/i);
  product.category = categoryMatch ? categoryMatch[1].toLowerCase() : 't-shirt';

  // Set pricing by category
  const pricing = {
    't-shirt': 25.99,
    'hoodie': 45.99,
    'mug': 15.99,
    'cap': 19.99,
    'stickers': 4.99
  };
  product.price = pricing[product.category];

  // Extract price if specified
  const priceMatch = section.match(/\$([0-9.]+)/);
  if (priceMatch) {
    product.price = parseFloat(priceMatch[1]);
  }

  // Extract description
  const descLines = section.split('
').filter(line => 
    line.trim() && 
    !line.match(/(category|price|\$)/i) &&
    line !== product.name
  );
  product.description = descLines.join(' ').substring(0, 200).trim();

  // Add Balding Pig branding
  product.tags = ['bald', 'humor', 'satirical', 'confidence', 'chrome-dome', 'balding-pig'];
  product.brand = 'The Balding Pig';

  // SEO optimization
  product.seo_title = `${product.name} - Balding Pig ${product.category}`;
  product.seo_description = `${product.description} Perfect for the confidently bald.`;

  products.push(product);
}

// Fallback if parsing fails
if (products.length === 0) {
  products.push({
    id: `bp_fallback_${Date.now()}`,
    name: 'Chrome Dome Champion',
    category: 't-shirt',
    price: 25.99,
    description: 'For the confidently bald warrior who embraces their aerodynamic advantage',
    tags: ['bald', 'humor', 'satirical', 'confidence'],
    brand: 'The Balding Pig',
    created: new Date().toISOString(),
    source: 'fallback_generation'
  });
}

return products.map(p => ({ json: p }));

// FINAL PRODUCT ASSEMBLY FOR ECOMMERCE
const product = $('supercode_parser').item.json;
const design = $('comfyui_design').item?.json;
const audio = $('elevenlabs_voice').item?.json;

// Create comprehensive product package
const finalProduct = {
  // Core product data
  id: product.id,
  name: product.name,
  category: product.category,
  price: product.price,
  description: product.description,
  brand: 'The Balding Pig',

  // Generated assets
  design_url: design?.output_images?.[0] || design?.image_url || `https://via.placeholder.com/512x512?text=${encodeURIComponent(product.name)}`,
  audio_url: audio?.audio_url || null,

  // E-commerce data
  sku: `BP-${product.category.toUpperCase()}-${product.id.slice(-6)}`,
  inventory: 50,
  weight: getWeight(product.category),
  dimensions: getDimensions(product.category),

  // Variants
  variants: getVariants(product.category),

  // SEO and marketing
  tags: product.tags,
  seo_title: product.seo_title,
  seo_description: product.seo_description,
  meta_keywords: product.tags.join(', '),

  // Platform readiness
  platforms: {
    shopify: { ready: true, status: 'draft' },
    etsy: { ready: true, status: 'draft' },
    facebook_shop: { ready: true, status: 'draft' },
    instagram_shop: { ready: true, status: 'draft' }
  },

  // Timestamps
  created_at: new Date().toISOString(),
  updated_at: new Date().toISOString(),

  // Source tracking
  generation_source: 'autonomous_ai_system',
  workflow_id: 'balding_pig_empire',
  trend_data: $('trend_search').item?.json
};

function getWeight(category) {
  const weights = {
    't-shirt': '0.2kg',
    'hoodie': '0.8kg',
    'mug': '0.4kg',
    'cap': '0.15kg',
    'stickers': '0.05kg'
  };
  return weights[category] || '0.2kg';
}

function getDimensions(category) {
  const dimensions = {
    't-shirt': '25x35x2cm',
    'hoodie': '30x40x5cm',
    'mug': '12x12x10cm',
    'cap': '20x20x15cm',
    'stickers': '10x15x0.5cm'
  };
  return dimensions[category] || '25x35x2cm';
}

function getVariants(category) {
  const variants = {
    't-shirt': [
      { name: 'Small', sku_suffix: 'S', price_modifier: 0 },
      { name: 'Medium', sku_suffix: 'M', price_modifier: 0 },
      { name: 'Large', sku_suffix: 'L', price_modifier: 0 },
      { name: 'X-Large', sku_suffix: 'XL', price_modifier: 2 },
      { name: 'XX-Large', sku_suffix: 'XXL', price_modifier: 4 }
    ],
    'hoodie': [
      { name: 'Small', sku_suffix: 'S', price_modifier: 0 },
      { name: 'Medium', sku_suffix: 'M', price_modifier: 0 },
      { name: 'Large', sku_suffix: 'L', price_modifier: 0 },
      { name: 'X-Large', sku_suffix: 'XL', price_modifier: 5 },
      { name: 'XX-Large', sku_suffix: 'XXL', price_modifier: 8 }
    ],
    'mug': [
      { name: '11oz Standard', sku_suffix: '11', price_modifier: 0 },
      { name: '15oz Large', sku_suffix: '15', price_modifier: 3 }
    ],
    'cap': [
      { name: 'One Size Fits Most', sku_suffix: 'OS', price_modifier: 0 }
    ],
    'stickers': [
      { name: 'Pack of 5', sku_suffix: 'P5', price_modifier: 0 },
      { name: 'Pack of 10', sku_suffix: 'P10', price_modifier: 3 }
    ]
  };
  return variants[category] || [{ name: 'Standard', sku_suffix: 'STD', price_modifier: 0 }];
}

return { json: finalProduct };
