// PRODUCT LISTING CREATOR
// Format product data for e-commerce platforms

const productData = {
  title: $json.name,
  category: $json.category,
  price: $json.target_price,
  description: $json.choices?.[0]?.message?.content || $json.description,
  tags: [
    'bald', 'baldness', 'humor', 'confidence', 'chrome dome',
    'follicularly challenged', 'the balding pig', $json.design_theme
  ],
  images: [
    `https://placeholder.com/400x400?text=${encodeURIComponent($json.name)}`
  ],
  variants: getVariants($json.category),
  inventory: 50,
  status: 'draft',
  created_date: new Date().toISOString(),
  platform_ready: true
};

function getVariants(category) {
  const variants = {
    't-shirt': ['S', 'M', 'L', 'XL', 'XXL'],
    'hoodie': ['S', 'M', 'L', 'XL', 'XXL'], 
    'mug': ['11oz', '15oz'],
    'cap': ['One Size'],
    'stickers': ['Pack of 5', 'Pack of 10']
  };
  return variants[category] || ['One Size'];
}

return { json: productData };
